import { useAuth0 } from '@auth0/auth0-react';
import React, { useEffect, useState } from 'react';
import ReactPaginate from 'react-paginate';
import Navbar from './Navbar';
import '../App.css'
import axios from 'axios';

// Example items, to simulate fetching from another resources.
const items = [];

useEffect(()=>{
    const { user } = useAuth0();
    axios.post("http://localhost:4000/getvendors",{
        userEmail : user.email
    }).then(response=>{
        console.log(response);
        // items = [...items,res]
    }).catch(error=()=> {
        alert("Error getting vendors..");
    })
},[]);

function Items({ currentItems }) {
  return (
    <>
      {currentItems &&
        currentItems.map((item) => (
          <div>
            <h3>Item #{item}</h3>
          </div>
        ))}
    </>
  );
}

function PaginatedItems({ itemsPerPage }) {
  // Here we use item offsets; we could also use page offsets
  // following the API or data you're working with.
  const [itemOffset, setItemOffset] = useState(0);

  // Simulate fetching items from another resources.
  // (This could be items from props; or items loaded in a local state
  // from an API endpoint with useEffect and useState)
  const endOffset = itemOffset + itemsPerPage;
  console.log(`Loading items from ${itemOffset} to ${endOffset}`);
  const currentItems = items.slice(itemOffset, endOffset);
  const pageCount = Math.ceil(items.length / itemsPerPage);

  // Invoke when user click to request another page.
  const handlePageClick = (event) => {
    const newOffset = (event.selected * itemsPerPage) % items.length;
    console.log(
      `User requested page number ${event.selected}, which is offset ${newOffset}`
    );
    setItemOffset(newOffset);
  };

  return (
    <>
      <Items currentItems={currentItems} />
      <ReactPaginate
        breakLabel="..."
        nextLabel="next >"
        onPageChange={handlePageClick}
        pageRangeDisplayed={5}
        pageCount={pageCount}
        previousLabel="< previous"
        renderOnZeroPageCount={null}
        containerClassName='pagination'
        pageLinkClassName='page-num'
        previousLinkClassName='page-num'
        nextLinkClassName='page-num'
        activeLinkClassName='active'
      />
    </>
  );
}

const Show = () => {
    return (
        <div id='container' className="App">
            <PaginatedItems itemsPerPage={4}  containerClassName={"pagination-container"} activeClassName={"active-page"}/>
        </div>
    );
}

function Vendors() {
    const {isAuthenticated} = useAuth0();
    return (
        <div>
            <Navbar/>
            {
                isAuthenticated ? <Show/> : <p1>Login first</p1>
            }
        </div>
    )
}
export default Vendors;
