import { useState } from "react";
import Navbar from "./Navbar";
import { useAuth0 } from "@auth0/auth0-react";

const center_div = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%,-50%)",
}

const formStyle = {
    minWidth : '300px'
}

const initialValues = {
    vendorName : "",
    bankAcntNo : "",
    bankName : "",
    addressOne : "",
    addressTwo : "",
    city : "",
    country : "",
    zipCode : ""
};

const ShowForm = () => {
    const [values, setValues] = useState(initialValues);
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setValues({
          ...values,
          [name]: value,
        });
      };
    const handleSubmit = (e) => {
        console.log(values);
        e.preventDefault();
    }
    return (
        <div style={center_div} >
            <h3 style={{marginLeft : "22%"}}>Add a Vendor</h3>
            <form onSubmit={handleSubmit}>
                <input placeholder="Vendor Name" type="text"style={formStyle} value={values.vendorName}  onChange={handleInputChange}></input><br/><br/>
                <input placeholder="Bank Account No." style={formStyle} value={values.bankAcntNo} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="Bank Name" style={formStyle} value={values.bankName} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="addressLineOne" style={formStyle} value={values.addressOne} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="addressLineTwo" style={formStyle} value={values.addressTwo} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="City" style={formStyle} value={values.city} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="Country" style={formStyle} value={values.country} onChange={handleInputChange}></input><br/><br/>
                <input placeholder="Zip Code" style={formStyle} value={values.zipCode} onChange={handleInputChange}></input><br/><br/>
                <button style={{minWidth : "90px",marginLeft : "33%"}} onChange={handleInputChange}>Submit</button>
            </form>
        </div>
    );
}

function AddVendor() {
    const { isAuthenticated } = useAuth0();
    return (
        <div>
            <Navbar />
            {
                !isAuthenticated ? <p>Please Login</p> :
                    <ShowForm />
            }
        </div>
    );
}

export default AddVendor;
