import { useState } from "react";
import Navbar from "./Navbar";
import { useAuth0 } from "@auth0/auth0-react";

const center_div = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%,-50%)",
}

const formStyle = {
    minWidth : '300px'
}


const ShowForm = () => {

    const [vendorName,setVendorName] = useState("");
    const [bankAcntNo,setbankAcntNo] = useState("");
    const [bankname,setbankname] = useState("");
    const [addressLineOne,setaddressLineOne] = useState("");
    const [addressLineTwo,setAddressLineTwo] = useState("");
    const [city,setcity] = useState("");
    const [country,setcountry] = useState("");
    const [zip,setzip] = useState("");

    const handleSubmit = (e) => {
        let values = {
            vendorName : vendorName,
            bankAcntNo 
        }
        console.log(values);
        e.preventDefault();
    }
    return (
        <div style={center_div} >
            <h3 style={{marginLeft : "22%"}}>Add a Vendor</h3>
            <form onSubmit={handleSubmit}>
                <input placeholder="Vendor Name" type="text"style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="Bank Account No." style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="Bank Name" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="addressLineOne" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="addressLineTwo" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="City" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="Country" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <input placeholder="Zip Code" style={formStyle} onChange={(e)=>{}}></input><br/><br/>
                <button style={{minWidth : "90px",marginLeft : "33%"}} onChange={(e)=>{}}>Submit</button>
            </form>
        </div>
    );
}

function AddVendor() {
    const { isAuthenticated } = useAuth0();
    return (
        <div>
            <Navbar />
            {
                !isAuthenticated ? <p>Please Login</p> :
                    <ShowForm />
            }
        </div>
    );
}

export default AddVendor;
