import Navbar from "./Navbar";
import { useAuth0 } from "@auth0/auth0-react";


const LoadingComponent = ()=> {
    const  {isAuthenticated}= useAuth0();
    return (
        <>
            <Navbar/>
            <h2>Hold back....</h2>
        </>
    );
}

const NotResponse = ()=> {
    return (
        <h1>Please Login to view profile</h1>
    );
}

const AuthResponse = ()=> {
    const { user } = useAuth0();
    return (
        <>
            <Navbar/>
            <img src={user.picture} alt={user.given_name}></img>
            <h1>{user.email}</h1>
            <h1>{user.given_name}</h1>
            <h1>{user.family_name}</h1>
        </>
    );
}

function Profile() {
    const {isLoading } = useAuth0();

    return (
        <div>
            {   
                isLoading ? <LoadingComponent/> 
                : <AuthResponse/>
            }
        </div>
    );
}

export default Profile;
