import { User, useAuth0 } from '@auth0/auth0-react';
import React, { useEffect, useState } from 'react';
import ReactPaginate from 'react-paginate';
import Navbar from './Navbar';
import '../App.css'
import axios from 'axios';
import { alpha } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import TableSortLabel from '@mui/material/TableSortLabel';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import Checkbox from '@mui/material/Checkbox';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import DeleteIcon from '@mui/icons-material/Delete';
import FilterListIcon from '@mui/icons-material/FilterList';
import { visuallyHidden } from '@mui/utils';


function Items({ currentItems }) {
    return (
        <>
            {currentItems &&
                currentItems.map((item,key) => (
                    <div key={key}>
                        {/* <h3>{item['vendorName']}</h3> */}

                    </div>
                ))}
        </>
    );
}

function PaginatedItems({ itemsPerPage }) {

    const [itemOffset, setItemOffset] = useState(0);

    const [items,setitems] = useState([]);
    const {isAuthenticated,user} = useAuth0();
    useEffect(()=>{
        isAuthenticated && axios.post('http://localhost:4000/getvendors',{
            email : user.email
        }).then(response=>{
            setitems(response['data']);
            // alert("got response")
        }).catch(err=>{
            console.log(err);
            alert("got error");
        })
    },[isAuthenticated]);
    const endOffset = itemOffset + itemsPerPage;
    const currentItems = items.slice(itemOffset, endOffset);
    const pageCount = Math.ceil(items.length / itemsPerPage);

    const handlePageClick = (event) => {
        const newOffset = (event.selected * itemsPerPage) % items.length;
        setItemOffset(newOffset);
    };

    return (
        <>
            <Items currentItems={currentItems} />
            <ReactPaginate
                breakLabel="..."
                nextLabel="next >"
                onPageChange={handlePageClick}
                pageRangeDisplayed={5}
                pageCount={pageCount}
                previousLabel="< previous"
                renderOnZeroPageCount={null}
                containerClassName='pagination'
                pageLinkClassName='page-num'
                previousLinkClassName='page-num'
                nextLinkClassName='page-num'
                activeLinkClassName='active'
            />
        </>
    );
}

const Show = () => {
    return (
        <div id='container' className="App">
            <PaginatedItems itemsPerPage={4} containerClassName={"pagination-container"} activeClassName={"active-page"} />
        </div>
    );
}

function Vendors() {
    const { isAuthenticated } = useAuth0();

    return (
        <div>
            <Navbar />
            {
                isAuthenticated ? <Show /> : <p>Login first</p>
            }
        </div>
    )
}
export default Vendors;
